mapper.select:
  	select * from (
 	select rownum r, aa.*
	from (select * from notice order by ndate desc) aa 
	) where r between #{s:INTEGER} and #{l:INTEGER}

    select * from (
 	select rownum r, aa.*
	from (select * from notice where aid = #{aid:VARCHAR} order by ndate desc) aa 
	) where r between #{s:INTEGER} and #{l:INTEGER}

mapper.update:
update notice 
set ndate=sysdate, ntitle=#{ntitle:VARCHAR}, ntext=#{ntext:VARCHAR}
where aid = #{aid:INTEGER} and nno = #{nno:INTEGER}
